package Bac2;

public class while1 {

	public static void main(String[] args) {
		int i=11;
		System.out.println("the odd numbers between 11 to 21");
		while(i<=21)
		{
			
			System.out.println(i);
			i+=2;
			
		}
		int a=4;
		System.out.println("the table of 4");
		while(a<=40)
		{
			
			System.out.println(a);
			a+=4;
			
		}
		int b=60;
		while(b>=0)
		{
			System.out.println(b);
			b-=6;
		}
		int a1=100;
		while (a1>=90)
		{
			System.out.println(a1);
			a1-=2;
		}
		
		
		
		
		// TODO Auto-generated method stub

	}

}
