package basics;

public class switch1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String d="swethaq";
		switch(d)
		{
		case "swetha":
			System.out.println("my name is swetha");
			break;
		case "kri":
			System.out.println("My name is kri");
			break;
			default:
				System.out.println("no nam e");
				break;
		}
		char c='3';
		switch(c)
		{
			case 'a':
				System.out.println("the char is a");
				break;
			case 'b':
				System.out.println("the char is b");
				break;
				default:
					System.out.println("No char");
					break;
				
				
		}
		

		

	}

}
