
package while1;

public class w1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=100,b=50,c=30;
		System.out.println("The value of a="+a);
		while(c<=b)
		{
			System.out.println(a);
			a+=2;
			c++;
		}
		System.out.println("The value of c is equal to b");
		}
	}


