package while1;

public class w6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 for(int i=0;i<=3;i++)
		 {
			 switch(i)
			 {
			 case 1:
				 System.out.println("The value of i is one");
				 break;
			 case 2:
				 System.out.println("The value of i is two");
				 break;
			 default:
				 System.out.println("The value of i is less than one or greater than 3");
			 }
		 }
	}

}
